export default function useStep(profileId: string) {
  const [stepIndex, setStepIndex] = useState(0);

  const buttons = useMemo(
    () => PROFILE_BUTTONS.data.find((item: ItemData) => item.id.includes(profileId))
    , [profileId]);

  const stepLength = useMemo(
    () => buttons && buttons.categories.length
    , [buttons]);

  const isLastStep = useMemo(
    () => !!(length) && (stepIndex === length - 1)
    , [stepIndex]);

  const handlePreviousStep = useCallback(() => {
    if (stepIndex > 0){
      setStepIndex(stepIndex - 1);
    }
  }, [setStepIndex, stepIndex]);

  const handleNextStep = useCallback(() => {
    if (stepLength && (stepIndex < stepLength - 1)){
      setStepIndex(stepIndex + 1);
    }
  }, [setStepIndex, stepIndex, stepLength]);

  return {
    stepIndex,
    setStepIndex,
    buttons,
    stepLength,
    isLastStep,
    handlePreviousStep,
    handleNextStep,
  };
}
```

