```ts
const findDeliveryPolicy = async (
    data: AddressData,
    requestParams: DeliveryPolicyProps,
  ) => {
    try {
      const res = await fetchDeliveryPolicyMock(requestParams);
      onResult(data, res);
    } catch (err) {
      // eslint-disable-next-line no-console
      console.error(err);
      Alert({ text: '일시적인 오류로 배송지 정보를 불러오지 못했습니다. 다시 시도해주세요.' });
